import React, { useEffect, useState } from "react";

function summary() {
    const summaryStats = () => {
        const [summary, setSummary] = useState(null);
        const [loading, setLoading] = useState(true);
        const [error, setError] = useState(null);
    }
    useEffect(() => {
        fetch("http://localhost:5000/summary/")
        .then((response) => {
            setSummary(response.data);
            setLoading(false);
        })
        .catch((err) => {
            console.error(err);
            setError("Error fetching summary stats");
            setLoading(false);
        });
  }, []);
  if (loading) return <p>Loading summary statistics...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Overall Spend Statistics</h2>
      <ul>
        <li>Total Spend: ${summary.overall_stats.total_spend.toFixed(2)}</li>
        <li>Average Transaction: ${summary.overall_stats.avg_transaction_amount.toFixed(2)}</li>
        <li>Max Spend: ${summary.overall_stats.max_spend.toFixed(2)}</li>
        <li>Min Spend: ${summary.overall_stats.min_spend.toFixed(2)}</li>
        <li>Number of Transactions: {summary.overall_stats.num_transactions}</li>
      </ul>

      <h2>Top Brands by Spend</h2>
      <table border="1" cellPadding="5">
        <thead>
          <tr>
            <th>Brand</th>
            <th>Total Spend</th>
            <th>Average Spend</th>
            <th>Transactions</th>
          </tr>
        </thead>
        <tbody>
          {summary.top_brands.map((brand) => (
            <tr key={brand.brand_name}>
              <td>{brand.brand_name}</td>
              <td>${brand.total_spend.toFixed(2)}</td>
              <td>${brand.avg_spend.toFixed(2)}</td>
              <td>{brand.num_transactions}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SummaryStats;
